import React, { useState, useEffect } from "react";
import "./About.css";
import Men from "../../assets/men.jpg";
import { IoArrowForwardOutline } from "react-icons/io5";
import { Link } from "react-router-dom";

function About() {
  const fullText = "→About Me←";
  const [displayText, setDisplayText] = useState("");
  const [index, setIndex] = useState(0);

  const fullParagraph =
    "My name is Collin Haws. I am a recent graduate of a Full-stack development bootcamp through the University of Utah. I have years of professional experience in technical support, finance, underwriting and more. I am hoping to leverage my career experience and new technical skills into a new opportunity in tech.";
  const [displayParagraph, setDisplayParagraph] = useState("");
  const [paragraphIndex, setParagraphIndex] = useState(0);
  const [isHeadingTyped, setIsHeadingTyped] = useState(false);

  useEffect(() => {
    if (index < fullText.length) {
      const timer = setTimeout(() => {
        setDisplayText(displayText + fullText[index]);
        setIndex(index + 1);
      }, 200);

      return () => clearTimeout(timer);
    } else {
      setIsHeadingTyped(true);
    }
  }, [displayText, index]);

  useEffect(() => {
    if (isHeadingTyped && paragraphIndex < fullParagraph.length) {
      const timer = setTimeout(() => {
        setDisplayParagraph(displayParagraph + fullParagraph[paragraphIndex]);
        setParagraphIndex(paragraphIndex + 1);
      }, 50);

      return () => clearTimeout(timer);
    }
  }, [displayParagraph, paragraphIndex, isHeadingTyped]);

  return (
    <section className="about__container" id="about">
      <picture>
        <img src={Men} alt="" />
      </picture>

      <div className="about__content">
        <h1 className="heading__one">{displayText}</h1>
        <p>{displayParagraph}</p>
        <p>
          Click "Resume" at the top of the page to get a copy of my downloadable
          resume and Contact me if there is a good fit! You can also view my
          portfolio of projects by also clicking the link in the header
        </p>
        <Link to="/experience" className="button-link">
          <button>
            <span>More</span>
            <IoArrowForwardOutline />
          </button>
        </Link>
      </div>
    </section>
  );
}

export default About;
